import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.io.OutputStream;
import java.nio.charset.StandardCharsets;

public class QQEmailPushAPI {

    // 请求URL
    private static final String REQUEST_URL = &quot;https://api.mrcwoods.com/qqyx/&quot;;

    public static void main(String[] args) {
        // 必填参数
        String send = &quot;your_send_qq_email&quot;; // 发件人QQ邮箱
        String pass = &quot;your_send_email_password&quot;; // 发件邮箱的授权码
        String receive = &quot;your_receive_qq_email&quot;; // 收件人QQ邮箱
        String mtie = &quot;your_email_subject&quot;; // 邮件标题【大标题】
        String title = &quot;your_email_content_title&quot;; // 内容标题【右侧小标题】
        String content = &quot;your_email_content&quot;; // 发件内容

        // 可选参数（注释掉的参数可以根据需要取消注释并赋值）
        // String param1 = &quot;value1&quot;;
        // String param2 = &quot;value2&quot;;

        try {
            // 发送请求
            String response = sendRequest(send, pass, receive, mtie, title, content);
            System.out.println(&quot;Response: &quot; + response);
        } catch (Exception e) {
            System.err.println(&quot;Failed to send request: &quot; + e.getMessage());
        }
    }

    /**
     * 发送HTTP请求到QQ邮件推送API
     *
     * @param send    发件人QQ邮箱
     * @param pass    发件邮箱的授权码
     * @param receive 收件人QQ邮箱
     * @param mtie    邮件标题【大标题】
     * @param title   内容标题【右侧小标题】
     * @param content 发件内容
     * @return API响应
     * @throws Exception 如果请求失败
     */
    private static String sendRequest(String send, String pass, String receive, String mtie, String title, String content) throws Exception {
        URL url = new URL(REQUEST_URL);
        HttpURLConnection connection = (HttpURLConnection) url.openConnection();
        connection.setRequestMethod(&quot;POST&quot;);
        connection.setRequestProperty(&quot;Content-Type&quot;, &quot;application/x-www-form-urlencoded&quot;);
        connection.setDoOutput(true);

        // 构建请求参数
        StringBuilder postData = new StringBuilder();
        postData.append(&quot;send=&quot;).append(encode(send));
        postData.append(&quot;&amp;pass=&quot;).append(encode(pass));
        postData.append(&quot;&amp;receive=&quot;).append(encode(receive));
        postData.append(&quot;&amp;mtie=&quot;).append(encode(mtie));
        postData.append(&quot;&amp;title=&quot;).append(encode(title));
        postData.append(&quot;&amp;content=&quot;).append(encode(content));

        // 可选参数可以根据需要添加
        // postData.append(&quot;&amp;param1=&quot;).append(encode(param1));
        // postData.append(&quot;&amp;param2=&quot;).append(encode(param2));

        try (OutputStream os = connection.getOutputStream()) {
            byte[] input = postData.toString().getBytes(StandardCharsets.UTF_8);
            os.write(input, 0, input.length);
        }

        // 检查响应码
        int responseCode = connection.getResponseCode();
        if (responseCode != HttpURLConnection.HTTP_OK) {
            throw new RuntimeException(&quot;Failed : HTTP error code : &quot; + responseCode);
        }

        // 读取响应
        try (BufferedReader br = new BufferedReader(new InputStreamReader(connection.getInputStream(), StandardCharsets.UTF_8))) {
            StringBuilder response = new StringBuilder();
            String line;
            while ((line = br.readLine()) != null) {
                response.append(line);
            }
            return response.toString();
        }
    }

    /**
     * URL编码
     *
     * @param value 需要编码的字符串
     * @return 编码后的字符串
     */
    private static String encode(String value) {
        try {
            return java.net.URLEncoder.encode(value, StandardCharsets.UTF_8.toString());
        } catch (Exception e) {
            throw new RuntimeException(&quot;Failed to encode value: &quot; + value, e);
        }
    }
}

