func QQEmailPush(send, pass, receive, mtie, title, content string) error {
	// 构建请求参数
	params := url.Values{
		&quot;send&quot;:    {send},    // 发件人QQ邮箱
		&quot;pass&quot;:    {pass},    // 发件邮箱的授权码
		&quot;receive&quot;: {receive}, // 收件人QQ邮箱
		&quot;mtie&quot;:    {mtie},    // 邮件标题【大标题】
		&quot;title&quot;:   {title},   // 内容标题【右侧小标题】
		&quot;content&quot;: {content}, // 发件内容
	}

	// 可选参数示例，注释掉的参数可以根据需要取消注释并设置值
	// params.Add(&quot;optionalParam&quot;, &quot;optionalValue&quot;)

	// 构建请求URL
	requestURL := &quot;https://api.mrcwoods.com/qqyx/&quot;

	// 发送POST请求
	resp, err := http.PostForm(requestURL, params)
	if err != nil {
		return fmt.Errorf(&quot;发送请求时出错: %v&quot;, err)
	}
	defer resp.Body.Close()

	// 读取响应体
	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return fmt.Errorf(&quot;读取响应体时出错: %v&quot;, err)
	}

	// 检查响应状态码
	if resp.StatusCode != http.StatusOK {
		return fmt.Errorf(&quot;请求失败，状态码: %d, 响应内容: %s&quot;, resp.StatusCode, string(body))
	}

	// 解析响应内容（假设响应内容为JSON格式）
	var response map[string]interface{}
	if err := json.Unmarshal(body, &amp;response); err != nil {
		return fmt.Errorf(&quot;解析响应内容时出错: %v&quot;, err)
	}

	// 打印响应内容
	fmt.Println(&quot;响应内容:&quot;, response)

	return nil
}

