import requests

def call_claude4_5_hiku_api(token, question, uid=None, type=None, system=None):
    &quot;&quot;&quot;
    调用Claude4.5-hikuAPI接口

    :param token: 登录获取用户密钥
    :param question: 对话内容
    :param uid: 六位数字，用来存储记忆对话(云端存储数据，每天零点准时清理，需要重新创建)，不填写则使用默共享记忆，创建速率限制为每60秒1个
    :param type: 返回格式，支持json和text，不填写默认返回text
    :param system: 模型提示词
    :return: API响应内容
    &quot;&quot;&quot;
    url = &quot;https://api.mrcwoods.com/v1/hiku-4.5/&quot;
    
    # 必填参数
    params = {
        &quot;token&quot;: token,
        &quot;question&quot;: question
    }
    
    # 可选参数
    # params[&quot;uid&quot;] = uid
    # params[&quot;type&quot;] = type
    # params[&quot;system&quot;] = system
    
    try:
        response = requests.post(url, data=params)
        response.raise_for_status()  # 检查HTTP请求是否成功
        
        if params.get(&quot;type&quot;) == &quot;json&quot;:
            return response.json()
        else:
            return response.text
    
    except requests.exceptions.HTTPError as http_err:
        print(f&quot;HTTP error occurred: {http_err}&quot;)
    except requests.exceptions.ConnectionError as conn_err:
        print(f&quot;Connection error occurred: {conn_err}&quot;)
    except requests.exceptions.Timeout as timeout_err:
        print(f&quot;Timeout error occurred: {timeout_err}&quot;)
    except requests.exceptions.RequestException as req_err:
        print(f&quot;An error occurred: {req_err}&quot;)
    except ValueError as json_err:
        print(f&quot;JSON decode error: {json_err}&quot;)

