package main

import (
	&quot;encoding/json&quot;
	&quot;fmt&quot;
	&quot;io/ioutil&quot;
	&quot;net/http&quot;
	&quot;net/url&quot;
	&quot;strings&quot;
)

// Claude4.5-hikuAPI 请求结构体
type Claude45HikuAPIRequest struct {
	Token    string `url:&quot;token&quot;`    // 必填：登录获取用户密钥
	Question string `url:&quot;question&quot;` // 必填：对话内容
	UID      int    `url:&quot;uid,omitempty&quot;`      // 可选：请填写六位数字，用来存储记忆对话(云端存储数据，每天零点准时清理，需要重新创建)，不填写则使用默共享记忆，创建速率限制为每60秒1个
	Type     string `url:&quot;type,omitempty&quot;`     // 可选：返回格式，支持json和text，不填写默认返回text
	System   string `url:&quot;system,omitempty&quot;`   // 可选：模型提示词
}

// Claude4.5-hikuAPI 响应结构体
type Claude45HikuAPIResponse struct {
	// 根据实际返回格式定义字段
	Answer string `json:&quot;answer&quot;`
	Error  string `json:&quot;error&quot;`
}

func main() {
	// 创建请求实例并设置必填参数
	request := Claude45HikuAPIRequest{
		Token:    &quot;your_token_here&quot;, // 替换为实际的token
		Question: &quot;your_question_here&quot;, // 替换为实际的对话内容
		// UID:      123456, // 取消注释并设置实际的uid
		// Type:     &quot;json&quot;, // 取消注释并设置实际的返回格式
		// System:   &quot;your_system_prompt_here&quot;, // 取消注释并设置实际的模型提示词
	}

	// 构建URL参数
	params := url.Values{}
	if request.Token != &quot;&quot; {
		params.Add(&quot;token&quot;, request.Token)
	}
	if request.Question != &quot;&quot; {
		params.Add(&quot;question&quot;, request.Question)
	}
	if request.UID != 0 {
		params.Add(&quot;uid&quot;, fmt.Sprintf(&quot;%d&quot;, request.UID))
	}
	if request.Type != &quot;&quot; {
		params.Add(&quot;type&quot;, request.Type)
	}
	if request.System != &quot;&quot; {
		params.Add(&quot;system&quot;, request.System)
	}

	// 构建完整的请求URL
	apiURL := &quot;https://api.mrcwoods.com/v1/hiku-4.5/&quot;
	fullURL := fmt.Sprintf(&quot;%s?%s&quot;, apiURL, params.Encode())

	// 发送HTTP请求
	resp, err := http.Post(fullURL, &quot;application/x-www-form-urlencoded&quot;, strings.NewReader(params.Encode()))
	if err != nil {
		fmt.Printf(&quot;发送请求失败: %v\n&quot;, err)
		return
	}
	defer resp.Body.Close()

	// 读取响应内容
	body, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		fmt.Printf(&quot;读取响应内容失败: %v\n&quot;, err)
		return
	}

	// 根据返回格式处理响应
	var response Claude45HikuAPIResponse
	if request.Type == &quot;json&quot; || request.Type == &quot;&quot; {
		if err := json.Unmarshal(body, &amp;response); err != nil {
			fmt.Printf(&quot;解析JSON响应失败: %v\n&quot;, err)
			return
		}
		if response.Error != &quot;&quot; {
			fmt.Printf(&quot;API返回错误: %v\n&quot;, response.Error)
			return
		}
		fmt.Printf(&quot;API响应: %v\n&quot;, response.Answer)
	} else {
		fmt.Printf(&quot;API响应: %s\n&quot;, string(body))
	}
}

