&lt;?php

// API 请求URL
$requestUrl = &quot;https://api.mrcwoods.com/v1/glm4.6-ocr/&quot;;

// 必填参数
$question = &quot;提问的内容&quot;; // 请替换为实际提问内容

// 可选参数，根据需要取消注释并设置值
// $image = &quot;图片链接&quot;; // 需要识别的图片链接，请使用标准的图片后缀传入(推荐使用：jpg)
// $video = &quot;视频链接&quot;; // 需要识别的视频链接，请使用标准的视频后缀传入，视频越大响应时间越长（大于五分钟可能会超时，推荐使用：mp4）
// $type = &quot;text&quot;; // 支持text和json两种返回方式，不填写默认text，填true为流式输出

// 构建请求参数
$params = [
    &quot;question&quot; =&gt; $question,
    // &quot;image&quot; =&gt; $image,
    // &quot;video&quot; =&gt; $video,
    // &quot;type&quot; =&gt; $type,
];

// 初始化cURL会话
$ch = curl_init();

// 设置cURL选项
curl_setopt($ch, CURLOPT_URL, $requestUrl);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
curl_setopt($ch, CURLOPT_POST, true);
curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));

// 执行cURL请求
$response = curl_exec($ch);

// 检查cURL请求是否成功
if ($response === false) {
    $error = curl_error($ch);
    curl_close($ch);
    die(&quot;cURL请求失败: &quot; . $error);
}

// 关闭cURL会话
curl_close($ch);

// 解析响应
if (isset($params[&apos;type&apos;]) &amp;&amp; $params[&apos;type&apos;] === &apos;json&apos;) {
    $response = json_decode($response, true);
    if (json_last_error() !== JSON_ERROR_NONE) {
        die(&quot;JSON解析失败: &quot; . json_last_error_msg());
    }
}

// 输出响应
echo &quot;API响应: &quot;;
print_r($response);

?&gt;
