use reqwest::Client;
use std::error::Error;

#[tokio::main]
async fn main() -&gt; Result&lt;(), Box&lt;dyn Error&gt;&gt; {
    // 创建HTTP客户端
    let client = Client::new();

    // 必填参数
    let url = &quot;https://example.com/image.jpg&quot;; // 图片链接
    let text = &quot;抖音美好体&quot;; // 水印内容
    let doa = 1; // 水印方位
    let size = &quot;20&quot;; // 水印字体大小

    // 可选参数（默认注释掉，用户可根据需要取消注释）
    // let param1 = &quot;value1&quot;; // 可选参数1
    // let param2 = &quot;value2&quot;; // 可选参数2

    // 构建请求URL
    let request_url = format!(
        &quot;https://api.mrcwoods.com/v1/tpsytj/?url={}&amp;text={}&amp;doa={}&amp;size={}&quot;,
        url, text, doa, size
    );

    // 发送GET请求
    let response = client.get(&amp;request_url).send().await;

    match response {
        Ok(res) =&gt; {
            if res.status().is_success() {
                // 处理成功的响应
                let bytes = res.bytes().await?;
                // 保存图片或进行其他处理
                println!(&quot;图片水印添加成功，图片大小: {} bytes&quot;, bytes.len());
            } else {
                // 处理HTTP错误
                eprintln!(&quot;请求失败，状态码: {}&quot;, res.status());
                let error_text = res.text().await?;
                eprintln!(&quot;错误信息: {}&quot;, error_text);
            }
        }
        Err(e) =&gt; {
            // 处理网络错误
            eprintln!(&quot;请求过程中发生错误: {}&quot;, e);
        }
    }

    Ok(())
}
