/**
 * 调用DeepSeek-ocrAPI进行OCR识别
 *
 * @param array $params 请求参数
 * @return string 返回结果
 * @throws Exception 请求异常时抛出
 */
function callDeepSeekOcrApi($params) {
    // 必填参数
    if (empty($params[&apos;question&apos;])) {
        throw new Exception(&quot;参数 &apos;question&apos; 不能为空&quot;);
    }

    // 请求URL
    $url = &quot;https://api.mrcwoods.com/v1/deepseek-ocr/&quot;;

    // 构建请求参数
    $query = http_build_query([
        &apos;question&apos; =&gt; $params[&apos;question&apos;],
        // &apos;image&apos; =&gt; &apos;&apos;, // 可选参数：需要识别的图片链接
        // &apos;video&apos; =&gt; &apos;&apos;, // 可选参数：需要识别的视频链接
        // &apos;type&apos; =&gt; &apos;&apos;, // 可选参数：支持text和json两种返回方式，不填写默认text，填true为流式输出
    ]);

    // 请求方式
    $requestMethod = &apos;POST&apos;; // 支持GET/POST，这里默认使用POST

    // 初始化cURL会话
    $ch = curl_init();

    if ($requestMethod === &apos;POST&apos;) {
        // 设置POST请求
        curl_setopt($ch, CURLOPT_POST, 1);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $query);
    } else {
        // 设置GET请求
        $url .= &apos;?&apos; . $query;
    }

    // 设置请求URL
    curl_setopt($ch, CURLOPT_URL, $url);

    // 设置超时时间
    curl_setopt($ch, CURLOPT_TIMEOUT, 30);

    // 设置是否返回响应头
    curl_setopt($ch, CURLOPT_HEADER, 0);

    // 设置返回结果为字符串
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);

    // 设置不验证SSL证书
    curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);
    curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);

    // 执行请求
    $response = curl_exec($ch);

    // 检查错误
    if (curl_errno($ch)) {
        throw new Exception(&apos;cURL错误: &apos; . curl_error($ch));
    }

    // 获取HTTP状态码
    $httpCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);
    if ($httpCode !== 200) {
        throw new Exception(&quot;请求失败，HTTP状态码: &quot; . $httpCode);
    }

    // 关闭cURL会话
    curl_close($ch);

    return $response;
}

try {
    // 示例调用
    $result = callDeepSeekOcrApi([
        &apos;question&apos; =&gt; &apos;需要识别的内容&apos;,
        // &apos;image&apos; =&gt; &apos;http://example.com/image.jpg&apos;,
        // &apos;video&apos; =&gt; &apos;http://example.com/video.mp4&apos;,
        // &apos;type&apos; =&gt; &apos;json&apos;,
    ]);
    echo $result;
} catch (Exception $e) {
    echo &apos;错误: &apos; . $e-&gt;getMessage();
}

