&lt;?php

// 必填参数
$token = &apos;YOUR_TOKEN_HERE&apos;; // 登录获取用户密钥
$msg = &apos;YOUR_PROMPT_TEXT_HERE&apos;; // 图片生成提示词
$url = &apos;YOUR_IMAGE_URL_HERE&apos;; // 上传的图片链接

// 可选参数
// $gs = &apos;h&apos;; // 图片宽高比（h为横屏(4:3)、s为竖屏(3:4)、f为正方形(1:1)，不填写默认为h）
// $type = &apos;json&apos;; // 返回的数据格式，支持json、text、img，不填写默认为json

// 请求URL
$requestUrl = &apos;https://api.mrcwoods.com/v1/nano-banana/&apos;;

// 初始化请求参数
$params = [
    &apos;token&apos; =&gt; $token,
    &apos;msg&apos; =&gt; $msg,
    &apos;url&apos; =&gt; $url,
    // &apos;gs&apos; =&gt; $gs, // 取消注释以使用此参数
    // &apos;type&apos; =&gt; $type, // 取消注释以使用此参数
];

// 初始化cURL会话
$ch = curl_init();

// 设置cURL选项
curl_setopt($ch, CURLOPT_URL, $requestUrl);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
curl_setopt($ch, CURLOPT_POST, true);
curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));

// 执行cURL请求
$response = curl_exec($ch);

// 检查cURL错误
if (curl_errno($ch)) {
    $errorMessage = &apos;cURL error: &apos; . curl_error($ch);
    curl_close($ch);
    throw new Exception($errorMessage);
}

// 关闭cURL会话
curl_close($ch);

// 解析响应
try {
    $responseArray = json_decode($response, true);
    if (json_last_error() !== JSON_ERROR_NONE) {
        throw new Exception(&apos;Invalid JSON response: &apos; . $response);
    }
} catch (Exception $e) {
    throw new Exception(&apos;Failed to decode response: &apos; . $e-&gt;getMessage());
}

// 处理响应数据
if (isset($responseArray[&apos;error&apos;])) {
    throw new Exception(&apos;API error: &apos; . $responseArray[&apos;error&apos;]);
}

// 输出响应结果
echo &apos;API Response: &apos;;
print_r($responseArray);

?&gt;
