&lt;?php

/**
 * 图片内容描述API调用示例
 * 快速掌握图片中的关键信息
 */

// 必填参数
$url = &apos;https://example.com/image.jpg&apos;; // 需要识别的图片链接

// 可选参数
// $type = &apos;json&apos;; // 返回数据格式，支持json和text，不填写默认返回text

// 请求URL
$requestUrl = &apos;https://api.mrcwoods.com/v1/tpnrms/&apos;;

// 初始化cURL会话
$ch = curl_init();

// 设置cURL选项
curl_setopt($ch, CURLOPT_URL, $requestUrl);
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

// 构建请求参数
$params = [
    &apos;url&apos; =&gt; $url,
    // &apos;type&apos; =&gt; $type, // 取消注释以使用可选参数
];

// 检查请求方法
$method = &apos;POST&apos;; // 可以根据需要更改为GET
if ($method === &apos;POST&apos;) {
    curl_setopt($ch, CURLOPT_POST, true);
    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));
} elseif ($method === &apos;GET&apos;) {
    $requestUrl .= &apos;?&apos; . http_build_query($params);
    curl_setopt($ch, CURLOPT_URL, $requestUrl);
} else {
    die(&apos;错误：不支持的请求方法&apos;);
}

// 执行cURL请求
$response = curl_exec($ch);

// 检查cURL错误
if (curl_errno($ch)) {
    $errorMessage = &apos;cURL错误: &apos; . curl_error($ch);
    curl_close($ch);
    die($errorMessage);
}

// 获取HTTP状态码
$httpStatusCode = curl_getinfo($ch, CURLINFO_HTTP_CODE);

// 关闭cURL会话
curl_close($ch);

// 检查HTTP状态码
if ($httpStatusCode !== 200) {
    die(&apos;错误：HTTP请求失败，状态码 &apos; . $httpStatusCode);
}

// 输出响应结果
echo &apos;API响应结果: &apos; . $response;

?&gt;
