&lt;?php

/**
 * 调用万能语言翻译API进行语言翻译
 *
 * @param string $msg 翻译内容，支持世界各国语言(AI自动检测)
 * @param string $target 目标语言(支持中文名或小字母缩写)，当target=list时输出语言列表，不填写默认翻译成英文
 * @param string $type 返回格式，支持json和text，不填写默认返回json
 * @return mixed
 */
function translateText($msg, $target = &apos;&apos;, $type = &apos;&apos;) {
    // 必填参数
    $params = [
        &apos;msg&apos; =&gt; $msg
    ];

    // 可选参数，用户可根据需要取消注释并设置值
    // $params[&apos;target&apos;] = $target; // 目标语言
    // $params[&apos;type&apos;] = $type; // 返回格式

    // 请求URL
    $url = &quot;https://api.mrcwoods.com/v1/wnyyfy/&quot;;

    // 初始化cURL会话
    $ch = curl_init();

    // 设置cURL选项
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
    curl_setopt($ch, CURLOPT_POST, true);
    curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($params));

    // 执行cURL请求
    $response = curl_exec($ch);

    // 检查cURL错误
    if (curl_errno($ch)) {
        $error_msg = &apos;cURL error: &apos; . curl_error($ch);
        curl_close($ch);
        throw new Exception($error_msg);
    }

    // 关闭cURL会话
    curl_close($ch);

    // 根据返回格式解析响应
    if ($type === &apos;json&apos; || empty($type)) {
        $response_data = json_decode($response, true);
        if (json_last_error() !== JSON_ERROR_NONE) {
            throw new Exception(&apos;Invalid JSON response: &apos; . json_last_error_msg());
        }
        return $response_data;
    } else {
        return $response;
    }
}

try {
    // 示例调用
    $msg = &quot;Hello, World!&quot;;
    $result = translateText($msg);
    print_r($result);
} catch (Exception $e) {
    echo &apos;Error: &apos; . $e-&gt;getMessage();
}

?&gt;
