import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;

public class QQMailPushAPI {

    private static final String API_URL = &quot;https://yunzhiapi.cn/API/qqyx/index.php&quot;;

    /**
     * 发送QQ邮件
     *
     * @param token   登录获取用户密钥
     * @param send    发件人QQ邮箱
     * @param pass    发件邮箱的授权码
     * @param receive 收件人QQ邮箱
     * @param mtie    邮件标题【大标题】
     * @param title   内容标题【右侧小标题】
     * @param content 发件内容
     */
    public static void sendQQMail(String token, String send, String pass, String receive, String mtie, String title, String content) {
        try {
            // 构建请求参数
            String urlParameters = String.format(&quot;token=%s&amp;send=%s&amp;pass=%s&amp;receive=%s&amp;mtie=%s&amp;title=%s&amp;content=%s&quot;,
                    URLEncoder.encode(token, StandardCharsets.UTF_8),
                    URLEncoder.encode(send, StandardCharsets.UTF_8),
                    URLEncoder.encode(pass, StandardCharsets.UTF_8),
                    URLEncoder.encode(receive, StandardCharsets.UTF_8),
                    URLEncoder.encode(mtie, StandardCharsets.UTF_8),
                    URLEncoder.encode(title, StandardCharsets.UTF_8),
                    URLEncoder.encode(content, StandardCharsets.UTF_8));

            // 创建URL对象
            URL url = new URL(API_URL);
            HttpURLConnection connection = (HttpURLConnection) url.openConnection();
            connection.setRequestMethod(&quot;POST&quot;); // 可根据需要改为GET
            connection.setDoOutput(true);

            // 设置请求头
            connection.setRequestProperty(&quot;Content-Type&quot;, &quot;application/x-www-form-urlencoded; charset=UTF-8&quot;);
            connection.setRequestProperty(&quot;Accept&quot;, &quot;text/plain&quot;);

            // 发送请求参数
            try (var outputStream = connection.getOutputStream()) {
                outputStream.write(urlParameters.getBytes(StandardCharsets.UTF_8));
            }

            // 获取响应码
            int responseCode = connection.getResponseCode();
            if (responseCode == HttpURLConnection.HTTP_OK) {
                // 读取响应内容
                try (BufferedReader in = new BufferedReader(new InputStreamReader(connection.getInputStream(), StandardCharsets.UTF_8))) {
                    String inputLine;
                    StringBuilder response = new StringBuilder();
                    while ((inputLine = in.readLine()) != null) {
                        response.append(inputLine);
                    }
                    System.out.println(&quot;邮件发送成功: &quot; + response.toString());
                }
            } else {
                System.err.println(&quot;邮件发送失败, 响应码: &quot; + responseCode);
                try (BufferedReader in = new BufferedReader(new InputStreamReader(connection.getErrorStream(), StandardCharsets.UTF_8))) {
                    String inputLine;
                    StringBuilder response = new StringBuilder();
                    while ((inputLine = in.readLine()) != null) {
                        response.append(inputLine);
                    }
                    System.err.println(&quot;错误信息: &quot; + response.toString());
                }
            }
        } catch (Exception e) {
            System.err.println(&quot;邮件发送过程中发生异常: &quot; + e.getMessage());
            e.printStackTrace();
        }
    }

    public static void main(String[] args) {
        // 必填参数
        String token = &quot;your_token_here&quot;;
        String send = &quot;your_send_email_here&quot;;
        String pass = &quot;your_authorization_code_here&quot;;
        String receive = &quot;your_receive_email_here&quot;;
        String mtie = &quot;your_main_title_here&quot;;
        String title = &quot;your_sub_title_here&quot;;
        String content = &quot;your_email_content_here&quot;;

        // 调用发送邮件方法
        sendQQMail(token, send, pass, receive, mtie, title, content);
